import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = (int) 1e9 + 7;
    static int n;
    static char[] s;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        s = br.readLine().toCharArray();
        int ans = 1;
        int cnt = 0;
        for (int i = 0; i < 2 * n; i++) {
            if (s[i] == 'B') {
                cnt++;
            }
        }
        if (cnt % 2 == 1) {
            pw.println(0);
            pw.close();
            return;
        }
        for (int i = 0; i < 2 * n; i++) {
            if (s[i] == 'B') {
                ans = (int) ((long) ans * (i + 1) % MOD);
            }
        }
        for (int i = 0; i < 2 * n; i++) {
            if (s[i] == 'W') {
                ans = (int) ((long) ans * (2 * n - i) % MOD);
            }
        }
        for (int i = 0; i < 2 * n - 1; i++) {
            if (s[i] == 'B' && s[i + 1] == 'W') {
                ans = (int) ((long) ans * (i + 1) % MOD);
            }
        }
        for (int i = 0; i < 2 * n - 1; i++) {
            if (s[i] == 'W' && s[i + 1] == 'B') {
                ans = (int) ((long) ans * (2 * n - i - 1) % MOD);
            }
        }
        pw.println(ans);
        pw.close();
    }
}
