#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

int n, h, w;
int r[MAXN], c[MAXN], a[MAXN];
int row[MAXN], col[MAXN];

int main() {
    scanf("%d %d %d", &n, &h, &w);
    for (int i = 0; i < n; i++) {
        scanf("%d %d %d", &r[i], &c[i], &a[i]);
        r[i]--;
        c[i]--;
    }

    for (int i = 0; i < n; i++) {
        row[r[i]] = max(row[r[i]], a[i]);
        col[c[i]] = max(col[c[i]], a[i]);
    }

    long long ans = 0;
    for (int i = 0; i < h; i++) {
        ans += row[i];
    }
    for (int i = 0; i < w; i++) {
        ans += col[i];
    }

    for (int i = 0; i < n; i++) {
        ans -= a[i];
    }

    printf("%lld\n", ans);
    return 0;
}

int max(int a, int b) {
    return a > b ? a : b;
}
