#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, h, w;
    cin >> n >> h >> w;

    vector<vector<int>> r(n), c(n), a(n);
    for (int i = 0; i < n; i++) {
        int ri, ci, ai;
        cin >> ri >> ci >> ai;
        r[ri - 1].push_back(i);
        c[ci - 1].push_back(i);
        a[i] = ai;
    }

    vector<int> row(h), col(w);
    for (int i = 0; i < h; i++) {
        for (int j : r[i]) {
            row[i] = max(row[i], a[j]);
        }
    }
    for (int i = 0; i < w; i++) {
        for (int j : c[i]) {
            col[i] = max(col[i], a[j]);
        }
    }

    int ans = 0;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            ans = max(ans, row[i] + col[j]);
        }
    }

    cout << ans << endl;

    return 0;
}
