#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m, l, r;
    cin >> n >> m >> l >> r;

    long long ans = 0;
    for (int i = m; i <= r - (n - m); i++) {
        long long c = power(i, n - m);
        long long d = power(i - m + 1, n - m - 1);
        long long e = power(n - m, n - m);
        long long f = power(n - m - 1, n - m - 1);
        long long g = (r - i + 1) - (l - i);
        long long h = (r - i + 1) - (l - i + 1);
        ans = (ans + c * d % MOD * e % MOD * f % MOD * g % MOD * h % MOD) % MOD;
    }

    cout << ans << endl;
    return 0;
}
