#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 50

int main() {
    int n;
    double v[N], dp[N][N];

    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%lf", &v[i]);
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[i][j] = v[i] + v[j];
        }
    }

    for (int k = 0; k < n - 1; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int l = 0; l < n; l++) {
                    if (l != i && l != j) {
                        dp[i][l] = dp[i][l] > (dp[i][j] + dp[j][l]) / 2 ? dp[i][l] : (dp[i][j] + dp[j][l]) / 2;
                    }
                }
            }
        }
    }

    double max = 0;
    for (int i = 0; i < n; i++) {
        max = max > dp[i][i] ? max : dp[i][i];
    }

    printf("%.10lf\n", max);

    return 0;
}
