#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 2e5 + 5;

int n, q, a[N], b[N], p[N], x[N], sz[N], ans[N];
vector<int> e[N];

void dfs(int u, int fa) {
    sz[u] = 1;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u);
        sz[u] += sz[v];
    }
}

void add(int u, int x) {
    ans[u] += x;
    for (int v : e[u]) {
        if (sz[v] * 2 > sz[u]) add(v, x);
    }
}

void solve() {
    cin >> n >> q;
    for (int i = 1; i < n; i++) {
        cin >> a[i] >> b[i];
        e[a[i]].push_back(b[i]);
    }
    for (int i = 1; i <= q; i++) {
        cin >> p[i] >> x[i];
    }
    dfs(1, 0);
    for (int i = q; i >= 1; i--) {
        add(p[i], x[i]);
    }
    for (int i = 1; i <= n; i++) {
        cout << ans[i] << " ";
    }
    cout << endl;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    int T = 1;
    // cin >> T;
    while (T--) {
        solve();
    }
    return 0;
}
