import java.io.*;
import java.util.*;

public class Main {
    static int N, Q;
    static int[] a, b, p, x, sz, ans;
    static List<Integer>[] g;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        Q = Integer.parseInt(st.nextToken());

        a = new int[N];
        b = new int[N];
        p = new int[Q];
        x = new int[Q];
        sz = new int[N];
        ans = new int[N];

        g = new List[N];
        for (int i = 0; i < N; i++) {
            g[i] = new ArrayList<>();
        }

        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            a[i] = Integer.parseInt(st.nextToken()) - 1;
            b[i] = Integer.parseInt(st.nextToken()) - 1;
            g[a[i]].add(b[i]);
            g[b[i]].add(a[i]);
        }

        for (int i = 0; i < Q; i++) {
            st = new StringTokenizer(br.readLine());
            p[i] = Integer.parseInt(st.nextToken()) - 1;
            x[i] = Integer.parseInt(st.nextToken());
        }

        dfs(0, -1);

        for (int i = 0; i < Q; i++) {
            add(p[i], x[i]);
        }

        for (int i = 0; i < N; i++) {
            pw.print(ans[i] + " ");
        }

        pw.close();
    }

    static void dfs(int u, int par) {
        sz[u] = 1;
        for (int v : g[u]) {
            if (v != par) {
                dfs(v, u);
                sz[u] += sz[v];
            }
        }
    }

    static void add(int u, int x) {
        for (int v : g[u]) {
            if (sz[v] * 2 > sz[u]) {
                add(v, x);
            }
        }
        ans[u] += x * sz[u];
    }
}
