import sys
input = sys.stdin.readline

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [0] * (4 * n)

    def update(self, node, start, end, index, value):
        if start == end:
            self.tree[node] += value
            return
        mid = (start + end) // 2
        if index <= mid:
            self.update(2 * node, start, mid, index, value)
        else:
            self.update(2 * node + 1, mid + 1, end, index, value)
        self.tree[node] = self.tree[2 * node] + self.tree[2 * node + 1]

    def query(self, node, start, end, left, right):
        if start > right or end < left:
            return 0
        if start >= left and end <= right:
            return self.tree[node]
        mid = (start + end) // 2
        return self.query(2 * node, start, mid, left, right) + self.query(2 * node + 1, mid + 1, end, left, right)

def dfs(node, parent, tree, graph):
    size[node] = 1
    for child in tree[node]:
        if child != parent:
            dfs(child, node, tree, graph)
            size[node] += size[child]
    nodes[node] = [node]

def build(node, start, end, tree, graph):
    if start == end:
        tree[node] = SegmentTree(1)
        return
    mid = (start + end) // 2
    build(2 * node, start, mid, tree, graph)
    build(2 * node + 1, mid + 1, end, tree, graph)
    tree[node] = SegmentTree(end - start + 1)

def update(node, start, end, index, value, tree, graph):
    if start == end:
        tree[node].update(1, 0, 0, 0, value)
        return
    mid = (start + end) // 2
    if index <= mid:
        update(2 * node, start, mid, index, value, tree, graph)
    else:
        update(2 * node + 1, mid + 1, end, index, value, tree, graph)
    tree[node].update(1, 0, 0, 0, tree[2 * node].query(1, 0, mid, 0, size[graph[index]] - 1) + tree[2 * node + 1].query(1, mid + 1, end, size[graph[index]], end))

def query(node, start, end, left, right, tree, graph):
    if start > right or end < left:
        return 0
    if start >= left and end <= right:
        return tree[node].query(1, 0, end - start, left - start, right - start)
    mid = (start + end) // 2
    return query(2 * node, start, mid, left, right, tree, graph) + query(2 * node + 1, mid + 1, end, left, right, tree, graph)

n, q = map(int, input().split())
graph = [0] + list(map(int, input().split()))[1::2]
tree = [[] for _ in range(n + 1)]
for i in range(n - 1):
    tree[graph[i + 1]].append(graph[i])
    tree[graph[i]].append(graph[i + 1])
size = [0] * (n + 1)
nodes = [[] for _ in range(n + 1)]
dfs(1, 0, tree, graph)
build(1, 0, n, tree, graph)
for _ in range(q):
    p, x = map(int, input().split())
    update(1, 0, n, p, x, tree, graph)
for i in range(1, n + 1):
    print(query(1, 0, n, i, i, tree, graph), end = ' ')
