#include <stdio.h>
#include <string.h>

int main() {
    char s[100001], t[100001];
    scanf("%s %s", s, t);
    int n = strlen(s), m = strlen(t);
    int i = 0, j = 0;
    while (i < n && j < m) {
        if (s[i] == t[j]) j++;
        i++;
    }
    printf("%d\n", j == m ? i : -1);
    return 0;
}
