#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007

typedef long long ll;

ll pow_mod(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    ll L, R;
    scanf("%lld %lld", &L, &R);
    ll ans = 0;
    for (ll i = 1; i <= R; i++) {
        ll x = i;
        ll y = R;
        ll cnt = 0;
        while (x <= y) {
            ll t = y / x;
            cnt = (cnt + pow_mod(t + 1, MOD - 2) * (y - x + 1) % MOD) % MOD;
            x = (y / x + 1) * x;
            y = R;
        }
        ans = (ans + cnt * (R / i + 1) % MOD) % MOD;
    }
    printf("%lld\n", (ans + R - L + 1) % MOD);
    return 0;
}
