#include <stdio.h>
#include <string.h>

#define MOD 998244353
#define N 100000

long long fac[N + 1], inv[N + 1];
long long C(int n, int m) {
    if (n < m) return 0;
    return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
}

int main() {
    int n;
    char s[3 * N + 1];
    scanf("%d %s", &n, s);
    int cnt[3] = {0};
    for (int i = 0; i < 3 * n; i++) {
        cnt[s[i] - 'R']++;
    }
    fac[0] = 1;
    for (int i = 1; i <= 3 * n; i++) {
        fac[i] = fac[i - 1] * i % MOD;
    }
    inv[3 * n] = pow(fac[3 * n], MOD - 2, MOD);
    for (int i = 3 * n - 1; i >= 0; i--) {
        inv[i] = inv[i + 1] * (i + 1) % MOD;
    }
    long long ans = 1;
    for (int i = 0; i < n; i++) {
        int a = cnt[0] - i;
        int b = cnt[1] - i;
        int c = cnt[2] - i;
        if (a < 0 || b < 0 || c < 0) break;
        ans = ans * C(a + b + c, a) % MOD * C(a + b + c, b) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}

long long pow(long long a, long long b, long long mod) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % mod;
        a = a * a % mod;
        b >>= 1;
    }
    return res;
}
