#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MOD = 998244353;

long long powMod(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n;
    string s;
    cin >> n >> s;

    vector<int> cnt(3);
    for (char c : s) {
        cnt[c - 'R']++;
    }

    long long ans = 1;
    for (int i = 0; i < 3; i++) {
        ans = ans * cnt[i] % MOD;
    }

    for (int i = 0; i < n; i++) {
        if (s[i] == 'R') {
            s[i] = 'G';
            s[i + n] = 'R';
            s[i + 2 * n] = 'B';
        } else if (s[i] == 'G') {
            s[i] = 'B';
            s[i + n] = 'G';
            s[i + 2 * n] = 'R';
        } else {
            s[i] = 'R';
            s[i + n] = 'B';
            s[i + 2 * n] = 'G';
        }

        int cnt[3] = {0};
        for (char c : s) {
            cnt[c - 'R']++;
        }

        long long ways = 1;
        for (int j = 0; j < 3; j++) {
            ways = ways * cnt[j] % MOD;
        }

        ans = ans * ways % MOD;
    }

    cout << ans << endl;

    return 0;
}
