import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAXN = 300005;
    static int n;
    static char[] s;
    static int[] cnt = new int[3];
    static int[] fac = new int[MAXN];
    static int[] inv = new int[MAXN];
    static int[] invfac = new int[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        s = br.readLine().toCharArray();
        for (int i = 0; i < 3 * n; i++) {
            cnt[s[i] - 'R']++;
        }
        fac[0] = 1;
        for (int i = 1; i <= 3 * n; i++) {
            fac[i] = (int) ((long) fac[i - 1] * i % MOD);
        }
        inv[3 * n] = pow(fac[3 * n], MOD - 2);
        for (int i = 3 * n - 1; i >= 0; i--) {
            inv[i] = (int) ((long) inv[i + 1] * (i + 1) % MOD);
        }
        for (int i = 0; i < 3 * n; i++) {
            invfac[i] = (int) ((long) inv[i] * inv[i + 1] % MOD);
        }
        long ans = 1;
        for (int i = 0; i < n; i++) {
            int l = i * 3, r = (i + 1) * 3 - 1;
            int a = cnt[0], b = cnt[1], c = cnt[2];
            ans = (ans * fac[a] % MOD * fac[b] % MOD * fac[c] % MOD * invfac[a + b] % MOD * invfac[b + c] % MOD * invfac[c + a] % MOD * invfac[a + b + c] % MOD) % MOD;
        }
        pw.println(ans);
        pw.close();
    }

    static int pow(int a, int b) {
        int res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (int) ((long) res * a % MOD);
            }
            a = (int) ((long) a * a % MOD);
            b >>= 1;
        }
        return res;
    }
}
