#include <stdio.h>
#include <stdlib.h>

typedef long long ll;

int main() {
    int n;
    scanf("%d", &n);

    ll a[n], b[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%lld", &b[i]);
    }

    ll sum = 0;
    for (int i = 0; i < n; i++) {
        sum += a[i];
    }

    ll diff = 0;
    for (int i = 0; i < n; i++) {
        diff += abs(a[i] - b[i]);
    }

    if (sum % n != 0) {
        printf("-1\n");
        return 0;
    }

    ll avg = sum / n;
    ll ops = 0;
    for (int i = 0; i < n; i++) {
        if (a[i] != avg) {
            ops += abs(a[i] - avg);
        }
    }

    if (diff % 2 == 0 && ops % 2 == 0) {
        printf("%lld\n", ops / 2);
    } else {
        printf("-1\n");
    }

    return 0;
}
