#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

typedef long long ll;

const int N = 2e5 + 5;

ll n, a[N], b[N], sum[N], pre[N], suf[N];

void solve() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }
    for (int i = 1; i <= n; i++) {
        cin >> b[i];
    }
    for (int i = 1; i <= n; i++) {
        sum[i] = a[i] + sum[i - 1];
    }
    for (int i = 1; i <= n; i++) {
        pre[i] = a[i] + pre[i - 1];
    }
    for (int i = n; i >= 1; i--) {
        suf[i] = a[i] + suf[i + 1];
    }
    ll ans = 0;
    for (int i = 1; i <= n; i++) {
        if (sum[i - 1] + pre[i] + suf[i + 1] == b[i]) {
            continue;
        }
        ll l = 1, r = n;
        while (l < r) {
            ll mid = (l + r + 1) / 2;
            ll val = sum[mid - 1] + pre[mid] + suf[mid + 1];
            if (val == b[i]) {
                r = mid - 1;
            } else if (val < b[i]) {
                l = mid;
            } else {
                r = mid;
            }
        }
        ans += r - i + 1;
    }
    if (ans == 0) {
        cout << -1 << endl;
    } else {
        cout << ans << endl;
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
