import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    static class Pair implements Comparable<Pair> {
        long first;
        long second;

        public Pair(long first, long second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Long.compare(this.first, o.first);
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        PrintWriter out = new PrintWriter(System.out);
        int n = sc.nextInt();
        long[] a = new long[n + 1];
        long[] b = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            a[i] = sc.nextLong();
        }
        for (int i = 1; i <= n; i++) {
            b[i] = sc.nextLong();
        }
        long[] pref = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            pref[i] = pref[i - 1] + a[i];
        }
        long[] suff = new long[n + 1];
        for (int i = n; i >= 1; i--) {
            suff[i] = suff[i + 1] + b[i];
        }
        long ans = 0;
        for (int i = 1; i <= n; i++) {
            if (pref[i] == suff[i + 1]) {
                ans = 0;
                break;
            }
            if (pref[i] < suff[i + 1]) {
                long diff = suff[i + 1] - pref[i];
                long[] dp = new long[n + 1];
                for (int j = 1; j <= n; j++) {
                    dp[j] = Math.abs(pref[j] - suff[j + 1]);
                }
                ans += binarySearch(dp, diff);
            } else {
                long diff = pref[i] - suff[i + 1];
                long[] dp = new long[n + 1];
                for (int j = 1; j <= n; j++) {
                    dp[j] = Math.abs(pref[j] - suff[j + 1]);
                }
                ans += binarySearch(dp, diff);
            }
        }
        if (ans == 0) {
            out.println(-1);
        } else {
            out.println(ans);
        }
        out.close();
    }

    static long binarySearch(long[] arr, long target) {
        int low = 0, high = arr.length - 1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (arr[mid] == target) {
                return mid;
            } else if (arr[mid] < target) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        return low;
    }
}
