#include <stdio.h>
#include <stdlib.h>

#define N 100
#define M 100
#define NM 10000

int main() {
    int n, m;
    int a[N][M];
    int b[N][M];
    int c[N][M];

    scanf("%d %d", &n, &m);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            scanf("%d", &a[i][j]);
        }
    }

    // Step 1: Rearrange each row
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            int k = j;
            while (k > 0 && a[i][k - 1] > a[i][k]) {
                int temp = a[i][k - 1];
                a[i][k - 1] = a[i][k];
                a[i][k] = temp;
                k--;
            }
        }
    }

    // Step 2: Rearrange each column
    for (int j = 0; j < m; j++) {
        for (int i = 0; i < n; i++) {
            int k = i;
            while (k > 0 && a[k - 1][j] > a[k][j]) {
                int temp = a[k - 1][j];
                a[k - 1][j] = a[k][j];
                a[k][j] = temp;
                k--;
            }
        }
    }

    // Step 3: Rearrange each row again
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            int k = j;
            while (k > 0 && a[i][k - 1] > a[i][k]) {
                int temp = a[i][k - 1];
                a[i][k - 1] = a[i][k];
                a[i][k] = temp;
                k--;
            }
        }
    }

    // Print the result
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            b[i][j] = a[i][j];
        }
        for (int j = 0; j < m; j++) {
            c[i][j] = (m * (i + 1) - 1 - j) % NM + 1;
        }
        for (int j = 0; j < m; j++) {
            printf("%d ", b[i][j]);
        }
        printf("\n");
        for (int j = 0; j < m; j++) {
            printf("%d ", c[i][j]);
        }
        printf("\n");
    }

    return 0;
}
