#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> a(n, vector<int>(m));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            cin >> a[i][j];
        }
    }

    vector<int> b;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            b.push_back(a[i][j]);
        }
    }

    sort(b.begin(), b.end());

    vector<vector<int>> c(n, vector<int>(m));
    int k = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            c[i][j] = b[k++];
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            cout << c[i][j] << " ";
        }
        cout << endl;
    }

    for (int j = 0; j < m; j++) {
        for (int i = 0; i < n; i++) {
            cout << c[i][j] << " ";
        }
        cout << endl;
    }

    return 0;
}
