#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;
    string s;
    cin >> s;

    int mid = n / 2;
    if (n % 2 == 0) {
        if (s.substr(0, mid) > s.substr(mid, n - mid)) {
            reverse(s.begin(), s.end());
        }
    } else {
        if (k % 2 == 1) {
            reverse(s.begin(), s.end());
        }
        if (s.substr(0, mid) > s.substr(mid, n - mid)) {
            reverse(s.begin(), s.end());
        }
    }

    string ans = s;
    for (int i = 0; i < n; i++) {
        string t = s;
        reverse(t.begin(), t.begin() + i);
        reverse(t.begin() + i, t.end());
        reverse(t.begin(), t.begin() + n - i);
        reverse(t.begin() + n - i, t.end());
        if (t < ans) {
            ans = t;
        }
    }

    cout << ans << endl;

    return 0;
}
