import java.io.*;
import java.util.*;

public class Main {
    static final int N = 5005;
    static char[] s = new char[N];
    static char[] t = new char[N];
    static char[] u = new char[N * 2];
    static char[] ans = new char[N];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int k = Integer.parseInt(st.nextToken());
        String str = br.readLine();
        for (int i = 1; i <= n; i++) {
            s[i] = str.charAt(i - 1);
        }
        if (k % 2 == 0) {
            Arrays.sort(s, 1, n + 1);
            for (int i = 1; i <= n; i++) {
                System.out.print(s[i]);
            }
            return;
        }
        for (int i = 1; i <= n; i++) {
            t[i] = s[n - i + 1];
        }
        for (int i = 1; i <= n; i++) {
            u[i] = s[i];
        }
        for (int i = n + 1; i <= n * 2; i++) {
            u[i] = t[i - n];
        }
        for (int i = 1; i <= n; i++) {
            ans[i] = u[i];
        }
        int l = 1, r = n;
        while (l <= r) {
            int mid = (l + r) / 2;
            boolean flag = true;
            for (int i = 1; i <= n; i++) {
                if (s[i] != ans[i]) {
                    flag = false;
                    break;
                }
            }
            if (flag) {
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        for (int i = 1; i <= n; i++) {
            if (s[i] < ans[i]) {
                ans[i] = s[i];
            }
        }
        for (int i = 1; i <= n; i++) {
            System.out.print(ans[i]);
        }
    }
}
