#include <stdio.h>
#include <stdlib.h>

#define N 200005
#define M 20

typedef struct {
    int l, r;
    int val;
} node;

node tree[N * M];
int n, m, a[N], ans;

void build(int k, int l, int r) {
    tree[k].l = l;
    tree[k].r = r;
    if (l == r) {
        tree[k].val = 0;
        return;
    }
    int mid = (l + r) >> 1;
    build(k * 2, l, mid);
    build(k * 2 + 1, mid + 1, r);
    tree[k].val = tree[k * 2].val + tree[k * 2 + 1].val;
}

void update(int k, int l, int r, int x) {
    if (tree[k].l > r || tree[k].r < l) return;
    if (tree[k].l >= l && tree[k].r <= r) {
        tree[k].val++;
        return;
    }
    update(k * 2, l, r, x);
    update(k * 2 + 1, l, r, x);
    tree[k].val = tree[k * 2].val + tree[k * 2 + 1].val;
}

int query(int k, int l, int r) {
    if (tree[k].l > r || tree[k].r < l) return 0;
    if (tree[k].l >= l && tree[k].r <= r) return tree[k].val;
    return query(k * 2, l, r) + query(k * 2 + 1, l, r);
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    build(1, 1, n);
    for (int i = 1; i <= n; i++) {
        for (int j = i; j <= n; j++) {
            int k = a[j];
            int l = m;
            int cnt = 0;
            while (k >= 1 && l >= 1) {
                cnt += query(1, i, j - 1);
                update(1, i, j - 1, k);
                k--;
                l--;
            }
            ans += cnt;
        }
    }
    printf("%d\n", ans);
    return 0;
}
