#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 2e5 + 5;

int n, L, dp[N][N], a[N];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> L;
    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= L; j++) {
            if (i == 1) {
                dp[i][j] = (a[i] == j);
            } else {
                dp[i][j] = dp[i - 1][j];
                for (int k = 1; k <= min(i - 1, L - 1); k++) {
                    dp[i][j] += dp[i - k - 1][j - 1];
                }
            }
        }
    }

    long long ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = i; j <= n; j++) {
            for (int k = 1; k <= L; k++) {
                ans += dp[j][k];
            }
        }
    }

    cout << ans << '\n';

    return 0;
}
