import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            char[][] grid = new char[n][n];
            for (int i = 0; i < n; i++) {
                String row = scanner.next();
                for (int j = 0; j < n; j++) {
                    grid[i][j] = row.charAt(j);
                }
            }
            int max = 0;
            for (int i = 0; i < n; i++) {
                int count = 0;
                for (int j = 0; j < n; j++) {
                    if (grid[i][j] == '1') {
                        count++;
                        max = Math.max(max, count);
                    } else {
                        count = 0;
                    }
                }
            }
            for (int j = 0; j < n; j++) {
                int count = 0;
                for (int i = 0; i < n; i++) {
                    if (grid[i][j] == '1') {
                        count++;
                        max = Math.max(max, count);
                    } else {
                        count = 0;
                    }
                }
            }
            for (int k = 0; k < 2; k++) {
                for (int i = 0; i < n; i++) {
                    int count = 0;
                    for (int j = 0; j < n; j++) {
                        if (grid[i + j][i + j - j] == '1') {
                            count++;
                            max = Math.max(max, count);
                        } else {
                            count = 0;
                        }
                    }
                }
                for (int i = 0; i < n; i++) {
                    int count = 0;
                    for (int j = 0; j < n; j++) {
                        if (grid[i + j][n - 1 - i - j] == '1') {
                            count++;
                            max = Math.max(max, count);
                        } else {
                            count = 0;
                        }
                    }
                }
            }
            System.out.println(max);
        }
    }
}
