import sys

def solve():
    n, l = map(int, input().split())
    a = [0] + list(map(int, input().split()))
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(i, n + 1):
            if i == j:
                dp[i][j] = 1
            else:
                for k in range(i, j):
                    if a[k] == a[i] and dp[i][k] + dp[k + 1][j] > dp[i][j]:
                        dp[i][j] = dp[i][k] + dp[k + 1][j]
                if a[i] == a[j] and dp[i][j - 1] > dp[i][j]:
                    dp[i][j] = dp[i][j - 1]
    ans = 0
    for i in range(1, n + 1):
        for j in range(i, n + 1):
            if dp[i][j] >= l:
                ans += 1
    print(ans)

if __name__ == "__main__":
    T = 1
    for _ in range(T):
        solve()
