#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    int K, X;
    std::cin >> K >> X;

    int min = X - K + 1;
    int max = X + K - 1;

    if (min < 0) {
        std::cout << min << " ";
        min++;
    }

    for (int i = min; i <= max; i++) {
        std::cout << i << " ";
    }

    if (max < 0) {
        std::cout << max;
    }

    return 0;
}
