#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 100000
#define M 10

int main() {
    int n;
    char s[N][M + 1];

    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%s", s[i]);
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        int cnt[M] = {0};
        for (int j = 0; j < M; j++) {
            cnt[s[i][j] - 'a']++;
        }

        for (int j = i + 1; j < n; j++) {
            int match = 1;
            int cnt2[M] = {0};
            for (int k = 0; k < M; k++) {
                cnt2[s[j][k] - 'a']++;
            }

            for (int k = 0; k < M; k++) {
                if (cnt[k] != cnt2[k]) {
                    match = 0;
                    break;
                }
            }

            if (match) {
                ans++;
            }
        }
    }

    printf("%d\n", ans);

    return 0;
}
