def max_consecutive_ones(grid):
    n = len(grid)
    max_count = 0

    # Check horizontal
    for row in grid:
        count = 0
        for num in row:
            if num == '1':
                count += 1
                max_count = max(max_count, count)
            else:
                count = 0

    # Check vertical
    for col in range(n):
        count = 0
        for row in grid:
            if row[col] == '1':
                count += 1
                max_count = max(max_count, count)
            else:
                count = 0

    # Check diagonal (top-left to bottom-right)
    for i in range(n):
        count = 0
        for j in range(n):
            if grid[i][j] == '1':
                count += 1
                max_count = max(max_count, count)
            else:
                count = 0

    # Check diagonal (top-right to bottom-left)
    for i in range(n):
        count = 0
        for j in range(n - 1, -1, -1):
            if grid[i][j] == '1':
                count += 1
                max_count = max(max_count, count)
            else:
                count = 0

    return max_count


def main():
    while True:
        n = int(input())
        if n == 0:
            break
        grid = [input() for _ in range(n)]
        print(max_consecutive_ones(grid))


if __name__ == "__main__":
    main()
