#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;
    vector<string> s(n);
    for (int i = 0; i < n; i++) {
        cin >> s[i];
    }
    vector<int> cnt(26);
    for (int i = 0; i < n; i++) {
        for (char c : s[i]) {
            cnt[c - 'a']++;
        }
    }
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (s[i] == s[j]) {
                ans++;
            }
            for (int k = 0; k < 26; k++) {
                if (cnt[k] != 0 && cnt[k] != s[i].size() - cnt[k]) {
                    goto next;
                }
            }
            ans++;
            next:;
        }
    }
    cout << ans << endl;
    return 0;
}
