#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 100005

typedef struct {
    int a, b;
} Job;

int compare_jobs(const void *a, const void *b) {
    Job *ja = (Job *)a;
    Job *jb = (Job *)b;
    return (ja->a - jb->a);
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    Job jobs[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &jobs[i].a, &jobs[i].b);
    }

    qsort(jobs, n, sizeof(Job), compare_jobs);

    int dp[MAX_N];
    memset(dp, 0, sizeof(dp));

    for (int i = 0; i < n; i++) {
        for (int j = m; j >= jobs[i].a; j--) {
            dp[j] = dp[j] > dp[j - jobs[i].a] + jobs[i].b ? dp[j] : dp[j - jobs[i].a] + jobs[i].b;
        }
    }

    int max_reward = 0;
    for (int i = 0; i <= m; i++) {
        max_reward = max_reward > dp[i] ? max_reward : dp[i];
    }

    printf("%d\n", max_reward);

    return 0;
}
