#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

struct Job {
    int days;
    int reward;
};

bool compareJobs(Job a, Job b) {
    return a.days < b.days;
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<Job> jobs(n);
    for (int i = 0; i < n; i++) {
        cin >> jobs[i].days >> jobs[i].reward;
    }

    sort(jobs.begin(), jobs.end(), compareJobs);

    priority_queue<int> pq;
    int maxReward = 0;
    int days = 0;

    for (int i = 0; i < n; i++) {
        if (days + jobs[i].days <= m) {
            pq.push(jobs[i].reward);
            days += jobs[i].days;
            maxReward += jobs[i].reward;
        } else {
            if (!pq.empty()) {
                maxReward -= pq.top();
                pq.pop();
                pq.push(jobs[i].reward);
                maxReward += jobs[i].reward;
            }
        }
    }

    cout << maxReward << endl;

    return 0;
}
