import java.io.*;
import java.util.*;

public class Main {
    static class Job implements Comparable<Job> {
        int days;
        int reward;

        public Job(int days, int reward) {
            this.days = days;
            this.reward = reward;
        }

        @Override
        public int compareTo(Job o) {
            return Integer.compare(this.days, o.days);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());

        Job[] jobs = new Job[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            jobs[i] = new Job(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()));
        }

        Arrays.sort(jobs);

        int[] dp = new int[m + 1];
        for (int i = 0; i < n; i++) {
            for (int j = m; j >= jobs[i].days; j--) {
                dp[j] = Math.max(dp[j], dp[j - jobs[i].days] + jobs[i].reward);
            }
        }

        System.out.println(dp[m]);
    }
}
