#include <stdio.h>
#include <limits.h>

#define N 2500
#define M 5000
#define INF 0x3f3f3f3f

int n, m, p, dist[N + 1], dp[N + 1][N + 1], f[N + 1][N + 1];

int main() {
    int i, j, k, a, b, c;
    scanf("%d%d%d", &n, &m, &p);
    for (i = 1; i <= n; i++) {
        for (j = 1; j <= n; j++) {
            dist[i][j] = INF;
            dp[i][j] = 0;
            f[i][j] = 0;
        }
    }
    for (i = 1; i <= m; i++) {
        scanf("%d%d%d", &a, &b, &c);
        dist[a][b] = 1;
        f[a][b] = c;
    }
    for (k = 1; k <= n; k++) {
        for (i = 1; i <= n; i++) {
            for (j = 1; j <= n; j++) {
                if (dist[i][k] + dist[k][j] < dist[i][j]) {
                    dist[i][j] = dist[i][k] + dist[k][j];
                    dp[i][j] = dp[i][k] + dp[k][j] + f[i][k] + f[k][j];
                    f[i][j] = f[i][k] + f[k][j];
                } else if (dist[i][k] + dist[k][j] == dist[i][j]) {
                    dp[i][j] = dp[i][k] + dp[k][j] + f[i][k] + f[k][j];
                    f[i][j] = f[i][k] + f[k][j];
                }
            }
        }
    }
    int ans = 0;
    for (i = 1; i <= n; i++) {
        for (j = 1; j <= n; j++) {
            if (dist[i][j] != INF) {
                int t = dist[i][j] * p;
                if (dp[i][j] >= t) {
                    ans = dp[i][j] - t;
                } else {
                    ans = dp[i][j];
                }
            }
        }
    }
    if (ans == 0) {
        printf("-1\n");
    } else {
        printf("%d\n", ans);
    }
    return 0;
}
