from collections import deque
import sys

N, M, P = map(int, input().split())
graph = [[] for _ in range(N + 1)]
for _ in range(M):
    u, v, c = map(int, input().split())
    graph[u].append((v, c))

def bfs():
    dist = [float('inf')] * (N + 1)
    dist[1] = 0
    q = deque([1])
    while q:
        u = q.popleft()
        for v, c in graph[u]:
            if dist[v] == float('inf'):
                dist[v] = dist[u] + 1
                q.append(v)
    return dist

dist = bfs()
dp = [[-1] * (P + 1) for _ in range(N + 1)]
dp[1][0] = 0

for u in range(1, N + 1):
    for t in range(P + 1):
        if dp[u][t] == -1:
            continue
        for v, c in graph[u]:
            if dist[v] == dist[u] + 1:
                dp[v][t] = max(dp[v][t], dp[u][t] + c)

ans = 0
for t in range(P + 1):
    ans = max(ans, dp[N][t] - t * P if dp[N][t] >= t * P else dp[N][t])

if ans == -1:
    print(-1)
else:
    print(ans)
