#include <iostream>
#include <vector>

using namespace std;

int main() {
    int p;
    cin >> p;
    vector<int> a(p);
    for (int i = 0; i < p; i++) {
        cin >> a[i];
    }
    vector<int> b(p);
    for (int i = 0; i < p; i++) {
        b[i] = (i * a[i]) % p;
    }
    for (int i = 0; i < p; i++) {
        cout << (b[i] + p) % p << " ";
    }
    cout << endl;
    return 0;
}
