#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int count = 0;
    for (int i = 1; i <= n; i++) {
        int len = 0;
        int temp = i;
        while (temp > 0) {
            len++;
            temp /= 10;
        }
        if (len % 2 != 0) {
            count++;
        }
    }
    printf("%d\n", count);
    return 0;
}
