#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int N, K;
    scanf("%d %d", &N, &K);

    int A[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
    }

    int min_diff = 1000000000;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            int diff = abs(A[i] - A[j]);
            min_diff = (diff < min_diff) ? diff : min_diff;
        }
    }

    int max_div = 0;
    for (int i = 0; i < N; i++) {
        int div = A[i] / (min_diff / 2 + 1);
        max_div = (div > max_div) ? div : max_div;
    }

    for (int i = 0; i < N; i++) {
        int div = A[i] / max_div;
        if (div * max_div != A[i]) {
            printf("%d\n", max_div);
            return 0;
        }
    }

    int min_val = 1000000000;
    for (int i = 0; i < N; i++) {
        min_val = (A[i] < min_val) ? A[i] : min_val;
    }

    int max_div2 = 0;
    for (int i = 0; i < N; i++) {
        int div = min_val / (A[i] - min_val);
        max_div2 = (div > max_div2) ? div : max_div2;
    }

    printf("%d\n", max_div2);
    return 0;
}
