#include <stdio.h>
#include <stdlib.h>

#define MOD 998244353

typedef struct {
    int x, y;
} Point;

int compare_points(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    if (p1->x != p2->x) return p1->x - p2->x;
    return p1->y - p2->y;
}

int main() {
    int n;
    scanf("%d", &n);
    Point *points = (Point *)malloc(n * sizeof(Point));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &points[i].x, &points[i].y);
    }
    qsort(points, n, sizeof(Point), compare_points);
    long long ans = 0;
    int x = points[0].x, y = points[0].y;
    int cnt = 1;
    for (int i = 1; i < n; i++) {
        if (points[i].x == x && points[i].y == y) {
            cnt++;
        } else {
            ans = (ans + (long long)cnt * (cnt + 1) / 2) % MOD;
            x = points[i].x;
            y = points[i].y;
            cnt = 1;
        }
    }
    ans = (ans + (long long)cnt * (cnt + 1) / 2) % MOD;
    printf("%lld\n", ans);
    free(points);
    return 0;
}
