#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 998244353;

int main() {
    int n;
    cin >> n;

    vector<int> x(n), y(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i] >> y[i];
    }

    sort(x.begin(), x.end());
    sort(y.begin(), y.end());

    vector<int> left(n), right(n), up(n), down(n);
    left[0] = right[0] = up[0] = down[0] = 1;
    for (int i = 1; i < n; ++i) {
        left[i] = (left[i - 1] + 1) % MOD;
        right[i] = (right[i - 1] + 1) % MOD;
        up[i] = (up[i - 1] + 1) % MOD;
        down[i] = (down[i - 1] + 1) % MOD;
    }

    long long ans = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = i; j < n; ++j) {
            int l = lower_bound(x.begin(), x.end(), x[i]) - x.begin();
            int r = upper_bound(x.begin(), x.end(), x[j]) - x.begin() - 1;
            int u = lower_bound(y.begin(), y.end(), y[i]) - y.begin();
            int d = upper_bound(y.begin(), y.end(), y[j]) - y.begin() - 1;

            long long cur = 1;
            for (int k = l; k <= r; ++k) {
                cur = (cur * left[k]) % MOD;
            }
            for (int k = r + 1; k <= n - 1; ++k) {
                cur = (cur * right[k]) % MOD;
            }
            for (int k = u; k <= d; ++k) {
                cur = (cur * up[k]) % MOD;
            }
            for (int k = d + 1; k <= n - 1; ++k) {
                cur = (cur * down[k]) % MOD;
            }
            ans = (ans + cur) % MOD;
        }
    }

    cout << ans << endl;

    return 0;
}
