#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> p(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> p[i];
    }

    std::vector<int> sorted_p = p;
    std::sort(sorted_p.begin(), sorted_p.end());

    bool is_sorted = true;
    for (int i = 0; i < n; ++i) {
        if (p[i] != sorted_p[i]) {
            is_sorted = false;
            break;
        }
    }

    if (is_sorted) {
        std::cout << "YES" << std::endl;
    } else {
        bool found = false;
        for (int i = 0; i < n; ++i) {
            for (int j = i + 1; j < n; ++j) {
                std::swap(p[i], p[j]);
                std::sort(p.begin(), p.end());
                if (p == sorted_p) {
                    found = true;
                    break;
                }
                std::swap(p[i], p[j]);
            }
            if (found) {
                break;
            }
        }

        if (found) {
            std::cout << "YES" << std::endl;
        } else {
            std::cout << "NO" << std::endl;
        }
    }

    return 0;
}
