import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = scanner.nextInt();
        }
        scanner.close();

        int[] sorted = Arrays.copyOf(p, n);
        Arrays.sort(sorted);

        boolean isSorted = true;
        for (int i = 0; i < n; i++) {
            if (p[i] != sorted[i]) {
                isSorted = false;
                break;
            }
        }

        if (isSorted) {
            System.out.println("YES");
        } else {
            boolean found = false;
            for (int i = 0; i < n - 1; i++) {
                if (p[i] > p[i + 1]) {
                    found = true;
                    break;
                }
            }
            if (found) {
                System.out.println("NO");
            } else {
                System.out.println("YES");
            }
        }
    }
}
