#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);

    int a[n + 2];
    int b[n + 1];

    for (int i = 1; i <= n + 1; i++) {
        scanf("%d", &a[i]);
    }

    for (int i = 1; i <= n; i++) {
        scanf("%d", &b[i]);
    }

    long long max = 0;
    for (int i = 1; i <= n; i++) {
        long long min = (long long) a[i] + a[i + 1];
        long long max1 = (long long) b[i];
        long long max2 = (long long) b[i];

        if (max1 < min) {
            max1 = min;
        }

        if (max2 < a[i]) {
            max2 = a[i];
        }

        if (max2 < a[i + 1]) {
            max2 = a[i + 1];
        }

        if (max1 < max2) {
            max1 = max2;
        }

        max = max > (max1 + a[i] + a[i + 1]) ? max : (max1 + a[i] + a[i + 1]);
    }

    printf("%lld\n", max);
    return 0;
}
