#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    string s;
    cin >> s;

    int n = s.size();
    vector<int> dp(13, 0);
    dp[0] = 1;

    for (int i = 0; i < n; i++) {
        vector<int> ndp(13, 0);
        for (int j = 0; j < 13; j++) {
            if (s[i] == '?') {
                for (int k = 0; k < 10; k++) {
                    ndp[(j * 10 + k) % 13] = (ndp[(j * 10 + k) % 13] + dp[j]) % MOD;
                }
            } else {
                int num = s[i] - '0';
                ndp[(j * 10 + num) % 13] = (ndp[(j * 10 + num) % 13] + dp[j]) % MOD;
            }
        }
        dp = ndp;
    }

    int ans = 0;
    for (int i = 0; i < 13; i++) {
        ans = (ans + dp[i]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
