import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAX = 100000;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        String s = br.readLine();
        int n = s.length();
        long[][] dp = new long[n + 1][10];
        for (int i = 0; i < 10; i++) {
            dp[0][i] = 1;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < 10; j++) {
                if (s.charAt(i - 1) == '?') {
                    for (int k = 0; k < 10; k++) {
                        dp[i][j] += dp[i - 1][k];
                        dp[i][j] %= MOD;
                    }
                } else {
                    dp[i][j] += dp[i - 1][s.charAt(i - 1) - '0'];
                    dp[i][j] %= MOD;
                }
            }
        }
        long ans = 0;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                if ((i + j) % 13 == 5) {
                    ans += dp[n][i] * dp[n][j];
                    ans %= MOD;
                }
            }
        }
        pw.println(ans);
        pw.close();
    }
}
