MOD = 10**9 + 7
MAXN = 100005

def solve():
    s = input()
    n = len(s)
    dp = [[0] * 10 for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(n):
        for j in range(10):
            if i > 0:
                dp[i][j] += dp[i - 1][j]
                dp[i][j] %= MOD
            if s[i] == '?':
                for k in range(10):
                    if (j * 10 + k) % 13 == 5:
                        dp[i + 1][k] += dp[i][j]
                        dp[i + 1][k] %= MOD
            else:
                if (j * 10 + int(s[i])) % 13 == 5:
                    dp[i + 1][int(s[i])] += dp[i][j]
                    dp[i + 1][int(s[i])] %= MOD
    ans = 0
    for i in range(10):
        ans += dp[n][i]
        ans %= MOD
    print(ans)

if __name__ == '__main__':
    solve()
