#include <stdio.h>

int main() {
    int x, y, k;
    scanf("%d %d %d", &x, &y, &k);

    if (k >= x + y) {
        printf("%d\n", x + y);
    } else if (k >= abs(x - y)) {
        printf("%d\n", 2 * (x + y - k));
    } else if (k >= abs(x + y)) {
        printf("%d\n", 2 * (x + y - k));
    } else {
        printf("-1\n");
    }

    return 0;
}
