#include <iostream>
#include <queue>
#include <unordered_set>
#include <unordered_map>

using namespace std;

struct Node {
    int x, y, step;
};

int main() {
    int x, y, k;
    cin >> x >> y >> k;

    queue<Node> q;
    unordered_set<string> vis;
    unordered_map<string, int> dis;

    q.push({0, 0, 0});
    vis.insert("0,0");
    dis["0,0"] = 0;

    int dx[] = {k, k, -k, -k};
    int dy[] = {k, -k, k, -k};

    while (!q.empty()) {
        Node u = q.front();
        q.pop();

        if (u.x == x && u.y == y) {
            cout << u.step << endl;
            return 0;
        }

        for (int i = 0; i < 4; i++) {
            int nx = u.x + dx[i];
            int ny = u.y + dy[i];

            string key = to_string(nx) + "," + to_string(ny);
            if (vis.find(key) == vis.end()) {
                q.push({nx, ny, u.step + 1});
                vis.insert(key);
                dis[key] = u.step + 1;
            }
        }
    }

    cout << -1 << endl;

    return 0;
}
