#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int n, k;
int a[MAXN];
int cnt[MAXN];
int vis[MAXN];
int ans[MAXN];

int main() {
    scanf("%d %d", &n, &k);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        cnt[a[i]]++;
    }
    int p = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < k; j++) {
            int x = (i * k + j) % n;
            if (cnt[a[x]] > 0) {
                while (cnt[a[x]] > 0) {
                    ans[p++] = a[x];
                    cnt[a[x]]--;
                }
            } else {
                ans[p++] = a[x];
                cnt[a[x]]++;
            }
        }
    }
    for (int i = 0; i < p; i++) {
        printf("%d ", ans[i]);
    }
    printf("\n");
    return 0;
}
