#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MOD 998244353
#define N 1000005

typedef long long ll;

ll n, m, f[N][N], g[N][N];

int main() {
    scanf("%lld %lld", &n, &m);
    memset(f, 0, sizeof(f));
    memset(g, 0, sizeof(g));
    f[0][0] = 1;
    for (ll i = 0; i <= m; i++) {
        for (ll j = 0; j <= n; j++) {
            if (i > 0) {
                g[j][0] = (g[j][0] + f[j][i - 1]) % MOD;
            }
            if (j > 0) {
                g[j][1] = (g[j][1] + f[j - 1][i]) % MOD;
            }
            if (i > 0 && j > 0) {
                g[j][2] = (g[j][2] + f[j - 1][i - 1] * j) % MOD;
            }
        }
        memcpy(f, g, sizeof(g));
        memset(g, 0, sizeof(g));
    }
    ll ans = 0;
    for (ll i = 0; i <= n; i++) {
        ans = (ans + f[i][m]) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
