#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 998244353;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<long long> fac(n + 1);
    fac[0] = 1;
    for (int i = 1; i <= n; i++) fac[i] = fac[i - 1] * i % MOD;

    vector<long long> inv(n + 1);
    inv[n] = power(fac[n], MOD - 2);
    for (int i = n - 1; i >= 0; i--) inv[i] = inv[i + 1] * (i + 1) % MOD;

    vector<long long> C(n + 1);
    for (int i = 0; i <= n; i++) {
        C[i] = fac[i] * inv[i] % MOD * inv[n - i] % MOD;
    }

    vector<long long> dp(n + 1);
    for (int i = 0; i <= n; i++) {
        dp[i] = C[i] * C[n - i - 1] % MOD;
    }

    for (int i = 0; i <= min(n - 1, m); i++) {
        for (int j = 0; j <= i; j++) {
            dp[i + 1] = (dp[i + 1] + dp[i] * (m - i) % MOD * C[j] % MOD * C[i - j] % MOD * power(j + 1, MOD - 2) % MOD) % MOD;
        }
    }

    cout << dp[m] << endl;

    return 0;
}
