import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAXN = (int) 1e6 + 5;
    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());

        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[n] = qpow(fac[n], MOD - 2);
        for (int i = n - 1; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }

        long ans = 0;
        for (int i = 0; i <= Math.min(n - 1, m); i++) {
            long coef = C(m, i) * fac[i] % MOD * fac[n - i - 1] % MOD;
            long tmp = qpow(2, m - i) * fac[m - i] % MOD;
            ans = (ans + coef * tmp) % MOD;
        }
        pw.println(ans);
        pw.close();
    }

    static long qpow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long C(int n, int m) {
        if (n < m) return 0;
        return fac[n] * inv[m] % MOD * inv[n - m] % MOD;
    }
}
